<?phpnamespace Controllers;use Model\Admin;use MVC\Router;class LoginController{        public static function login(Router $router): void    {                if (isPostBack()){                        $auth = new Admin($_POST);            $errores = $auth->validar();                        if(empty($errores)){                //verificar si el usuario existe                $resultado = $auth->existeUsuario();                                if(!$resultado){                    //verifica si el usuario existe o no                    $errores = Admin::getErrores();                }else{                    //verificar el password                    $autenticado = $auth->comprobarPassword($resultado);                                        if ($autenticado){                        //autenticar al usuario                        $auth->autenticarUsuario();                    }else{                        $errores = Admin::getErrores();                    }                }                            }                    }                $router->render('auth/login',            [                "errores"=>$errores            ]);    }    public static function logout(Router $router): void    {        session_start();                $_SESSION = [];                header('Location: / ');    }    }