<?phpnamespace Controllers;use Model\Propiedad;use MVC\Router;use PHPMailer\PHPMailer\PHPMailer;class PaginasController{    public static function index(Router $router)    {        $propiedades = Propiedad::getUnsellPropertiesWithLimit(3);        $inicio = true;        $router->render('paginas/index', [            "propiedades" => $propiedades,            "inicio" => $inicio        ]);    }    public static function nosotros(Router $router)    {        $router->render('paginas/nosotros');    }    public static function propiedades(Router $router)    {        $propiedades = Propiedad::getUnsellPropertiesWithLimit(6);                $router->render('paginas/propiedades',            [                "propiedades" => $propiedades            ]);    }    public static function propiedad(Router $router)    {        $id = validarORedireccionar('/');        if ($id) {            $propiedad = Propiedad::find($id);        }        $router->render('paginas/propiedad',            [                "propiedad" => $propiedad            ]);    }    public static function blog(Router $router)    {        $router->render('paginas/blog');    }    public static function entrada(Router $router)    {        $router->render('paginas/entrada');    }    public static function contacto(Router $router)    {        $mensaje = null;                if (isPostBack()) {            $respuestas = $_POST['contacto'];            // crear una instancia de phpmailer            $mail = new PHPMailer();            //configurar SMTP            $mail->isSMTP();            $mail->Host = $_ENV['EMAIL_HOST'];            $mail->SMTPAuth = true;            $mail->Username = $_ENV['EMAIL_USER'];            $mail->Password = $_ENV['EMAIL_PASS'];            $mail->SMTPSecure = 'tls';            $mail->Port = $_ENV['EMAIL_PORT'];            //configurar el contenido del email             $mail->setFrom('admin@bienesraices.com');            $mail->addAddress('admin@bienesraices.com', 'Bienes Raices');            $mail->Subject = 'Tienes un nuevo mensaje';            //habilitat html            $mail->isHTML(true);            $mail->CharSet = 'utf-8';            //definir el contenido            $contenido = '<html>';            $contenido .= '<p>tienes un nuevo mensaje</p>';            $contenido .= '<p>Nombre: ' . $respuestas['nombre'] . '</p>';            $contenido .= '<p>Mensaje: ' . $respuestas['mensaje'] . '</p>';            $contenido .= '<p>Vende o Compra: ' . $respuestas['tipo'] . '</p>';            $contenido .= '<p>Precio o Presupuesto: ' . $respuestas['precio'] . '</p>';            if ($respuestas['contacto'] == 'telefono') {                $contenido .= '<p> Eligio ser contactado por telefono: </p>';                $contenido .= '<p>Telefono: ' . $respuestas['telefono'] . '</p>';                $contenido .= '<p>Fecha: ' . $respuestas['fecha'] . '</p>';                $contenido .= '<p>Hora: ' . $respuestas['hora'] . '</p>';            } else {                $contenido .= '<p> Eligio ser contactado por email: </p>';                $contenido .= '<p>Email: ' . $respuestas['email'] . '</p>';            }            $contenido .= '</html>';            $mail->Body = $contenido;            $mail->AltBody = "Esto es un texto alternativo";            //enviar el email            if ($mail->send()) {                $mensaje = 'El mensaje fue enviado';            } else {                $mensaje = 'Hubo un problema al enviar el mensaje';            }        }        $router->render('paginas/contacto',            [                "mensaje" => $mensaje            ]);    }}