<?phpnamespace Model;class Admin extends ActiveRecord{    protected static string $tabla = 'usuarios';    protected static array $columnaDb = ["id", "usuario", "password"];    public $id;    public $email;    public $password;    public function __construct($args = [])    {        $this->id = $args["id"] ?? null;        $this->email = $args["usuario"] ?? null;        $this->password = $args["password"] ?? null;    }    public function validar(): array    {        if (!$this->email) {            self::$errores[] = "El email es obligatorio";        }        if (!$this->password) {            self::$errores[] = "La contrasena es obligatorio";        }        return self::$errores;    }    public function existeUsuario()    {        //revisar si un usuario existe o no         $query = "SELECT * FROM " . self::$tabla . " WHERE usuario = '" . $this->email . "' LIMIT 1";        $resultado = self::$db->query($query);        if (!$resultado->num_rows) {            self::$errores[] = "El usuario no existe";            return;        }        return $resultado;    }    public function comprobarPassword($resultado): bool    {        $usuario = $resultado->fetch_object();        $autenticado = password_verify($this->password, $usuario->password);        if (!$autenticado) {            self::$errores[] = "El Password es incorrecto";        }        return $autenticado;    }    public function autenticarUsuario(): void    {        session_start();                $_SESSION['user'] = $this->email;         $_SESSION['login'] = true;                 header("Location: /admin");    }}