<fieldset>		<legend>Informacion general</legend>		<label for="nombre">Nombre:</label>		<input						type="text"						name="vendedor[nombre]"						id="nombre"						placeholder="Nombre Vendedor"						value="<?php echo s($vendedor->nombre); ?>"        <?php if ($readonly) {            echo "readonly";        } ?>		/>		<label for="apellido">Apellido:</label>		<input						type="text"						name="vendedor[apellido]"						id="apellido"						placeholder="Apellido Vendedor"						value="<?php echo s($vendedor->apellido); ?>"        <?php if ($readonly) {            echo "readonly";        } ?>>		<label for="telefono">Telefono:</label>		<input						type="tel"						name="vendedor[telefono]"						id="telefono"						placeholder="Telefono"						value="<?php echo s($vendedor->telefono) ?>"        <?php if ($readonly) {            echo "readonly";        } ?>>		<label for="imagen">Propidedad(es) asociada(s):</label>		<div class="seccion contenedor contenedor-anuncios">        <?php foreach ($propiedades as $propiedad) : ?>						<div>								<img src="/imagenes/<?php echo $propiedad->imagen; ?>" alt="img propiedad" class="imagen-small">								<span class="fw-300"> <?php echo $propiedad->titulo ?> </span>						</div>        <?php endforeach; ?>		</div></fieldset>